import React, {useState, useEffect} from "react";

const Timer = () => {
    const [time, setTime] = useState(0);

    useEffect(() => {
        // mounted
        const timer = setInterval(() => setTime(time+1),1000);

        return function() {
            clearInterval(timer)
        }

    }, [time]);

    return (
        <div>
            <h1>Stopwatch</h1>
            <p>Current Time is {time}</p>
        </div>
    );
};

export default Timer;
